import { Component } from '@angular/core';

@Component({
  selector: "sample",
  templateUrl: "./sample.component.html",
  styleUrls: ['./sample.component.css']
})

export class SampleComponent {
  public modifiedText: string = '';
  public sampleText: string = 'abc';

  whenclickIsDone() {
    this.modifiedText += this.sampleText
  }
}
<h1>hello</h1>
<p class="blah">Some Text</p>

<input type="text" [(ng-model)]="SampleComponent.sampleText">
<span></span>
<button class="btn btn-primary" (click)="whenclickIsDone()">Press me</button>
<span>{{modifiedText}}</span>
